INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001967, N'محمصات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأجهزة مطبخ لتحميص الخبز. تتكون المنتجات عادةً من فتحة واحدة أو أكثر يمكن خفض شرائح الخبز فيها ليتم طهيها بواسطة الحرارة الناتجة عن الأسلاك الكهربائية. قد تحتوي المنتجات أيضًا على ملحقات لتمكين تسخين الكرواسون أو المنتجات الأخرى.', N'يستثنى من ذلك منتجات مثل أفران التحميص والشوايات.')
