INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001982, N'مقالي (مدعوم)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مقلاة معدنية ذات قاع مستدير وغطاء ، والتي تسخن عند توصيلها بمصدر طاقة كهربائية. يتضمن منتجات مثل مقالي كهربائية آمنة لغسالة الأطباق ومقالي كهربائية غير لاصقة.', N'يستثنى من ذلك المنتجات مثل المقالي غير الكهربائية والطباخات الكهربائية.')
