INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003713, N'موزعات المياه - منضدية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حاوية مياه قائمة بذاتها تقوم بتوزيع المياه عند الطلب. هذه المنتجات شائعة في بيئات المنزل والمكتب ويمكنها صرف الماء الساخن والبارد. يشمل منتجات مثل موزعات المياه المكتبية.', N'لا يشمل المنتجات مثل مبردات المياه - قائمة بذاتها')
