INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007021, N'المنزل الذكي / معدات أتمتة المنزل - لوحة التحكم', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز مادي يُستخدم عادةً لأجهزة التحكم المنزلية الذكية التي يمكنها أتمتة مجموعة متنوعة من الأجهزة وعناصر التحكم داخل المنزل.', N'باستثناء أجهزة التحكم عن بعد العالمية وأجهزة المنزل الذكي / أتمتة المنزل.')
