INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007024, N'المنزل الذكي / معدات أتمتة المنزل - العشب / الحديقة / أجهزة الترفيه', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأجهزة للعشب / الحديقة / الترفيه مع تحكم منزلي ذكي. هذه هي الأجهزة التي تُستخدم لجدولة رشاشات حديقتك ، والعناية بالنباتات الخاصة بك.', N'يستثنى من ذلك مؤقتات الري / أجهزة التحكم وجميع أجهزة الحديقة / الحديقة / الترفيه التي ليست من معدات أتمتة المنزل / المنزل الذكي.')
