INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007025, N'المنزل الذكي / معدات أتمتة المنزل - أجهزة الإنارة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أجهزة إضاءة ذات تحكم منزلي ذكي.', N'يستثنى من ذلك جميع أجهزة الإضاءة التي ليست من معدات أتمتة المنزل / المنزل الذكي.')
