INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007022, N'المنزل الذكي / معدات أتمتة المنزل - أجهزة الأمن', N'يشمل أي منتجات يمكن وصفها / مراقبتها على أنها منتجات أمان / أمن / مراقبة مميزة مع تحكم منزلي ذكي. يمكن أن تشمل هذه الكاميرات وأجهزة الكشف عن المياه / الدخان.', N'يستثني جميع الأجهزة الأمنية التي ليست من معدات أتمتة المنزل / المنزل الذكي.')
