INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007023, N'المنزل الذكي / معدات أتمتة المنزل - أجهزة تنظيم درجة الحرارة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أجهزة تنظيم درجة الحرارة (التدفئة ، ومكيف الهواء ، والتهوية) مع التحكم الذكي في المنزل ، على سبيل المثال منتجات التدفئة الآلية المنزلية ، ومنتجات مكيفات الهواء المنزلية.', N'يستثني جميع أجهزة تنظيم درجة الحرارة التي ليست من معدات أتمتة المنزل / المنزل الذكي.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006768, N'باقات الاستروميريا', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها باقات عبارة عن مزيج من أنواع مختلفة من الزهور المقطوفة والخضراوات الطازجة التي لا تقل عن 75 في المائة على وجه التحديد من جنس Alstroemeria غير محدد على أنه أي نوع معين: يُعرف أيضًا باسم زنبق الإنكا أو زنبق بيرو .', N'باستثناء جميع الباقات الأخرى التي تم تصنيفها بالفعل.')
