INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006764, N'باقات القرنفل', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها باقات عبارة عن مزيج من أنواع مختلفة من الزهور المقطوفة والخضر الطازجة التي لا تقل عن 75 في المائة من جنس Dianthus الذي لم يتم تعريفه على أنه أي نوع محدد: يُعرف أيضًا باسم القرنفل.', N'باستثناء جميع الباقات الأخرى التي تم تصنيفها بالفعل. لا يشمل على وجه التحديد باقات ميني قرنفل.')
