INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006766, N'باقات الزنبق', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها باقات عبارة عن مزيج من أنواع مختلفة من الزهور المقطوفة والخضراوات الطازجة التي تشكل 75 بالمائة على الأقل من جنس Lilium غير المعرف على أنه أي نوع محدد: يُعرف أيضًا باسم الزنبق.', N'باستثناء جميع الباقات الأخرى التي تم تصنيفها بالفعل.')
