INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006765, N'باقات قرنفل صغيرة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها باقات عبارة عن مزيج من أنواع مختلفة من الزهور المقطوفة والخضر الطازجة التي تشكل 75 بالمائة على الأقل من جنس Dianthus على وجه التحديد من النوع الصغير: المعروف أيضًا باسم قرنفل صغير.', N'باستثناء جميع الباقات الأخرى التي تم تصنيفها بالفعل. يستثنى على وجه التحديد باقات القرنفل العادية.')
