INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000498, N'منتجات سلامة حمام الأطفال', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأجهزة تحمي الطفل من الإصابة أثناء الاستحمام. يشمل منتجات مثل واقيات الصنبور.', N'باستثناء منتجات مثل حمامات الأطفال وكراسي استحمام الأطفال والمهدات ، وجميع الألعاب المصممة / المخصصة للاستخدام في الحمام ، ومنتجات سلامة الحمام غير المصممة / المخصصة للأطفال مثل سجادات الاستحمام ومنتجات السلامة العامة للأطفال.')
