INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006797, N'المفروشات الصحية للأطفال - أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها تلك المستخدمة كأثاث صحي للأطفال غير المصنفة ضمن أي لبنة أخرى ضمن فئة المفروشات الصحية للأطفال.', N'يستثني أي منتجات أخرى للأثاث الصحي للأطفال في فئة المفروشات الصحية للأطفال مثل نونية الأطفال وحصائر تغيير الأطفال ومنتجات سلامة حمام الأطفال.')
