INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002224, N'بطانيات / يلقي (بدون كهرباء)', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها غطاء أو غلاف غير مزود بالطاقة للدفء أو الراحة.', N'يستثنى على وجه التحديد البطانيات / يلقي (بالطاقة). باستثناء المنتجات مثل الألحفة واللحف وبطانيات الحريق وأكياس النوم وبطانيات الطوارئ.')
