INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007721, N'أرجل الأثاث', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أرجل يمكن ربطها بقطعة أثاث (مثل المقاعد والطاولات والأسرة).', N'يستثنى على وجه التحديد عجلات الأثاث والوسادات والشرائح.')
