INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10004101, N'ساعات - قطع غيار', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جزء بديل للساعة. تشمل منتجات مثل حركات الساعة وأقراص الساعة.', N'باستثناء استبدال قطع غيار الساعة والساعة الكاملة.')
