INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002141, N'عبوات متنوعة لمعدات قياس الأغذية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها اثنين أو أكثر من أدوات قياس المطبخ المميزة التي تباع معًا ، والتي توجد داخل المخطط الذي ينتمي إلى طوب مختلف ولكن لنفس الفئة ، أي منتجين أو أكثر موجودان في نفس العبوة التي تتقاطع مع الطوب داخل فئة معدات قياس الغذاء. يشمل منتجات مثل مؤقت الطهي ومقياس الحرارة اللذين يتم بيعهما معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثني منتجات مثل ميزان المطبخ وحزمة أدوات المطبخ المتنوعة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002140, N'موازين حرارة الطعام', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة لقياس درجة حرارة الطعام أثناء الطهي. تشمل منتجات مثل موازين الحرارة الرقمية ومقاييس حرارة الطعام التي يتم تشغيلها يدويًا ومؤشرات درجة الحرارة التي يمكن التخلص منها المصممة لأطعمة معينة والنوافذ المنبثقة التي يتم إدخالها في الطعام والنافذة المنبثقة عند الوصول إلى درجة الحرارة المطلوبة.', N'باستثناء منتجات مثل موازين الحرارة غير المصممة خصيصًا لقياس درجة حرارة الطعام.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002139, N'معدات قياس حجم الغذاء', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أدوات مطبخ مصممة خصيصًا لقياس حجم المنتجات الغذائية ، والتي يشار إليها عادةً بالملاعق والأكواب ومقاييس السعة مثل مكاييل أو مليلتر. يشمل منتجات مثل قياس الملاعق وأباريق القياس وأوعية القياس.', N'يستثنى من ذلك منتجات مثل ملاعق التقديم وأكواب التقديم وأباريق التقديم المصنفة في أدوات المائدة المنزلية.')
