INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005429, N'قمع الطعام', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أنبوب أو أنبوب عريض من الأعلى وضيق في الأسفل ، يستخدم لصب الطعام السائل في فتحات صغيرة من البرطمانات أو الزجاجات. تشمل المنتجات المصنوعة من البلاستيك أو المعدن المخصص للاستخدام في الأطعمة.', N'يستثنى من ذلك منتجات مثل قمع الزيت المستخدمة في صناعة السيارات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005701, N'أوعية تحضير الطعام', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها وعاء يستخدم لتحضير الطعام. تشمل منتجات مثل أوعية خلط الكيك ، والأوعية المستخدمة لخفق البيض والقشدة والأوعية المستخدمة لخلط السلطات والمعكرونة.', N'يستثنى من ذلك المنتجات مثل أوعية التقديم والأوعية التي تستخدم لمرة واحدة')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005426, N'فرش تحضير الطعام / مضخات الزيت / آلة التحضير', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها فرشاة صغيرة أو ماصة أو مضخة رش تُستخدم في وضع الطلاء الزجاجي على الخبز والمعجنات والبسكويت ، بالإضافة إلى تحميص اللحوم والخضروات قبل الطهي أو بعده. تشمل منتجات مثل فرش المعجنات المصنوعة من شعيرات النايلون أو الشعيرات الطبيعية المعقمة أو ريش الإوز.', N'يستثني منتجات مثل فرش الطلاء.')
