INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007259, N'أطباق التقديم العميقة', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنه أطباق عميقة ، مما يسمح بتقديم الطعام على المائدة ، مثل الحساء أو السلطة أو الخضار.', N'لا يشمل أطباق التقديم المسطحة.')
