INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007246, N'مجموعة من النظارات', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنهما منتجان أو أكثر من منتجات الأواني الزجاجية المتميزة التي تخدم / تشرب تباع معًا ، والتي توجد داخل المخطط الذي ينتمي إلى طوب مختلف ولكن لنفس الفئة ، أي منتجين أو أكثر متضمنين في نفس العبوة التي تعبر الطوب داخل فئة الأواني الزجاجية. تشمل منتجات مثل النظارات والدورق التي تباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع الشراء من عملية اتخاذ قرار التصنيف.', N'')
