INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007270, N'عبوات متنوعة للأطباق (غير للاستعمال مرة واحدة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كقطعتين مميزتين أو أكثر من أدوات المائدة المباعة معًا ، والتي توجد داخل المخطط تنتمي إلى قوالب مختلفة ولكن تنتمي إلى نفس الفئة ، أي منتجين أو أكثر متضمنين في نفس العبوة التي تتقاطع مع الطوب ضمن فئة أطباق. تشمل منتجات مثل مجموعة أدوات المائدة بما في ذلك أدوات المائدة وأدوات المائدة للحلويات و / أو أطباق التقديم التي تُباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'باستثناء المنتجات التي تستخدم لمرة واحدة.')
