INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002160, N'عربات تقديم الطعام (بدون كهرباء)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها عربة ضيقة على عجلات يمكن دفعها يدويًا من مكان إلى آخر وتستخدم لحمل وتسليم الطعام والمشروبات. تشمل منتجات مثل عربات المضيفة الخشبية والمعدنية.', N'باستثناء المنتجات مثل الصواني وعربات التقديم التي تعمل بالطاقة.')
