INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002161, N'سخانات / مبردات الطعام / المشروبات (بدون كهرباء)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأدوات مائدة أو معدات تقديم مصممة خصيصًا للحفاظ على الطعام دافئًا / باردًا عن طريق تقنية العزل أو مصنوعة من مادة تحافظ على درجة حرارة ثابتة لفترة طويلة. يتم تسخين بعض سخانات الطعام غير الكهربائية بالشموع. يشمل منتجات مثل سخانات الطعام المصنوعة من السيراميك ، وحارق الشموع ، وسخانات الطعام من Tupperware.', N'باستثناء منتجات مثل سخانات / مبردات الطعام التي تعمل بالطاقة ودوارق التفريغ المصنفة في حاويات تخزين الأطعمة / المشروبات.')
