INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000635, N'أواني الطعام (يمكن التخلص منها)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها عنصر يمكن التخلص منه لتقديم الطعام و / أو تناوله. يشمل الأطباق والأوعية التي تستخدم لمرة واحدة.', N'يُستثنى من ذلك المنتجات مثل الفخار وأواني الطعام المصنوعة من البورسلين والأطباق التي لا تستخدم لمرة واحدة ، فضلاً عن أدوات مائدة التخييم التي لا تستخدم لمرة واحدة.')
