INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005693, N'رجاجة ملح / فلفل', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حاوية صغيرة مملوءة بالملح / الفلفل. بشكل عام ، تحتوي الهزازات على العديد من الثقوب الصغيرة أعلى الحاوية ، وهي صغيرة بما يكفي لتتطلب عملية اهتزاز للحصول على كمية ملحوظة من البهارات لتتخلص منها.', N'يستثنى من ذلك منتجات مثل مطاحن الملح والفلفل.')
