INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003234, N'نباتات / سماد تربة / غذاء', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مادة مضافة إلى التربة لزيادة إنتاجية النباتات والحيوانات. قد يتم تصميم هذه المنتجات لتغذية أشجار أو أنواع معينة من الأشجار. قد تكون هذه المنتجات متوفرة في شكل سائل أو صلب أو مسحوق. يشمل منتجات مثل سماد أشجار الفاكهة وأسمدة أشجار الزينة.', N'يستثنى من ذلك منتجات مثل مبيد الحشائش.')
