INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003225, N'برك / برك / برك / ميزات مائية أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجات بركة وميزات مائية ، حيث لا يكون مستخدم المخطط قادرًا على تصنيف المنتجات في الطوب الموجود داخل المخطط.', N'يستثنى من ذلك جميع منتجات البرك وخصائص المياه المصنفة حاليًا.')
