INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003224, N'حزم متنوعة في الحديقة / برك الحديقة / البرك / ميزات المياه', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها بركتان متميزتان أو أكثر من منتجات خصائص المياه التي تباع معًا ، والتي توجد داخل المخطط الذي ينتمي إلى طوب مختلف ولكن لنفس الفئة التي تتكون من منتجين أو أكثر متضمن في نفس العبوة التي تتقاطع مع الطوب ضمن فئة البرك وخصائص المياه. يشمل منتجات مثل ميزة المياه التي تُباع مع مرشح ميزة البركة / المياه. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يُستثنى من ذلك منتجات مثل ميزات المياه التي تُباع بمضخات المياه.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003220, N'مهويات البركة / الماء', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز مخصص لتزويد مياه البركة بالأكسجين من خلال استخدام مضخة لتقليل خطر الركود. تشمل منتجات مثل مهويات النافورة وأجهزة التهوية بالانتشار.', N'يستثنى من ذلك المنتجات مثل المضخات والضباب والنافورات.')
