INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003218, N'ضباب ميزة البركة / المياه', N'يشمل أي منتجات يمكن وصفها / مراقبتها كجهاز إلكتروني يهدف إلى تحويل الماء إلى ضباب أو ضباب للتأثير الجوي. قد تتضمن هذه الأجهزة أيضًا أضواء ملونة أو وامضة لإضفاء التأثير. يشمل منتجات مثل ضباب البركة في الحديقة ، ومضخات المياه الداخلية.', N'يستثنى من ذلك منتجات مثل المضخات وأجهزة التهوية.')
