INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005253, N'لوازم حمامات السباحة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمنتج يستخدم لتحسين جودة حمام السباحة. قد تعزز هذه المنتجات الصحة والسلامة أو مظهر حمام السباحة. تشمل منتجات مثل موزعات أقراص الكلور ومجموعات صيانة حمامات السباحة.', N'يستثنى من ذلك جميع المعالجات الكيميائية للمياه بالإضافة إلى أجهزة التهوية ومضخات البرك وأجهزة التبييض بالأشعة فوق البنفسجية.')
