INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003223, N'أغطية واقية لحمام السباحة / البركة / ميزة المياه', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها قطعة من المواد تهدف إلى وضعها فوق بركة أو ميزة مائية لحمايتها من التلوث الناجم عن المخلفات أو لحماية سكان البركة من الحيوانات المفترسة. تتشكل عادة من شبك أو فينيل أو بولي كلوريد الفينيل ولكنها قد تكون من مواد أكثر صلابة.', N'باستثناء المنتجات مثل بطانات البركة.')
