INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005476, N'فقاريات أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كحيوان به فقاريات ، حيث لا يستطيع مستخدم المخطط تصنيف المنتجات الموجودة في الطوب الموجود داخل المخطط.', N'يستثنى جميع الفقاريات المصنفة حاليا.')
