INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003219, N'أجهزة تنقية / معقمات للأشعة فوق البنفسجية لحمام السباحة / البركة / المياه', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز يستخدم الضوء فوق البنفسجي لإحداث جسيمات صغيرة مثل جراثيم الطحالب والبكتيريا والطفيليات ، الموجودة في مياه البركة ولكنها تمر عبر المرشحات العادية ، لتتجمع معًا حتى يمكن التقاطها المرشح وإزالته من التعليق.', N'باستثناء المنتجات مثل مرشحات البركة')
