INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007009, N'العشب الصناعي', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كغطاء أرضي مصنوع من العشب الصناعي (يُعرف أيضًا باسم Astroturf) ، وعادة ما يكون مصممًا للحديقة أو العشب. عادةً ما تكون هذه المنتجات مصنوعة من القماش والبلاستيك أو المواد الاصطناعية.', N'يستثنى من ذلك الزهور / النباتات / الأشجار الاصطناعية.')
