INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10004110, N'الحديقة / تربة الحديقة / تعديلات التربة أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمنتجات لتعديل تربة الحديقة والتربة ، حيث لا يتمكن مستخدم المخطط من تصنيف المنتجات الموجودة في الطوب الموجود داخل المخطط. يستثنى من ذلك كل الطوب المصنف حاليا.', N'')
