INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003235, N'نشارة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مادة مثل القش ونشارة الخشب والأوراق والأغشية البلاستيكية أو التربة الرخوة وشرائح اللحاء المنتشرة على سطح التربة لحماية التربة وجذور النباتات من تأثيرات قطرات المطر أو تقشر التربة أو تجميدها أو تبخرها. تشمل منتجات مثل النشارة العضوية ، وأقمشة التكسية الأرضية ، وأقمشة المناظر الطبيعية.', N'يستثنى من ذلك منتجات مثل السماد والأسمدة.')
