INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003236, N'التربة السطحية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها الطبقة العليا المزروعة من التربة ، والتي قد تختلف في العمق من 8 إلى 45 سم. يحتوي على مواد عضوية ، ومجموعة متنوعة من كائنات التربة ، بما في ذلك ديدان الأرض. يشمل منتجات مثل التربة السطحية للحشائش وتربة التربة السطحية.', N'يستثنى من ذلك منتجات مثل السماد والأسمدة.')
