INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005461, N'بقري (ماشية)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ثدييات مملة وبطيئة الحركة وثابتة. عادة ما تربى هذه الحيوانات كماشية لأغراض اللحوم والألبان والجلود. تشمل الحيوانات مثل ماشية اللعاب والأنجوس.', N'يستثنى من ذلك الحيوانات مثل الأغنام والماعز والخيول.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005625, N'كلاب (كلاب)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أي من الثدييات المتشعبة المختلفة ذات المخالب غير القابلة للانكماش والكمامات الطويلة عادةً. تطورت هذه الحيوانات إلى مئات السلالات المختلفة وهي من الحيوانات الأليفة المستأنسة شائعة جدًا في جميع أنحاء العالم. يشمل منتجات مثل الألزاسي وكذلك بيتبول تيرير.', N'يستثنى من ذلك منتجات مثل القطط والقوارض')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005462, N'كابرين (الماعز)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أو تتعلق أو تشبه ماعز أو ماعز. يمكن أن يشمل ذلك أيًا من المجترات الرشيقة العديدة المتعلقة بالأغنام ولكن لها لحية وقرون مستقيمة. يشمل الماعز الجبلي.', N'يستثنى من ذلك الأبقار والأغنام وكذلك جميع الحيوانات ذات القرون الأخرى.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005469, N'الحيتان (الحيتان / الدلافين / خنازير البحر)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أي من الثدييات المائية البحرية المختلفة من رتبة الحيتان ، والتي تتميز بجسم شبه خالٍ من الشعر ، وأطراف أمامية معدلة إلى زعانف عريضة ، وأطراف خلفية أثرية ، وذيل مسطح. تشمل الحيتان والدلافين وخنازير البحر.', N'يستثنى من ذلك الأسماك والزواحف.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005463, N'الخيول (الخيول / الحمير)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ثدييات ذات حوافر لها أرجل نحيلة وطبقة مسطحة مع عرف ضيق على طول الجزء الخلفي من الرقبة. تشمل منتجات مثل الحمير الوحشية والفحول والبغال.', N'يستثنى من ذلك منتجات مثل الأبقار والأغنام والماعز.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005460, N'القطط (القطط)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كحيوان من أنواع القطط الصغيرة إلى متوسطة الحجم مقارنة بالقطط الكبيرة أو جنس Panthera ، والذي يشير إلى النمور والأسود والجاغوار والنمور. القطط حيوانات لاحمة. تطورت هذه الحيوانات إلى مئات السلالات المختلفة وهي من الحيوانات الأليفة المستأنسة شائعة جدًا في جميع أنحاء العالم. يشمل الماكر مثل القط المنزلي وقط مانكس.', N'يستثنى من ذلك منتجات مثل الرئيسيات والجرابيات وكذلك الكلاب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005456, N'أسماك', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها فقاريات مائية تتنفس الخياشيم ، بشكل عام ذات زعانف ، وهي مناسبة للتنقل والتغذية في الماء. يشمل منتجات مثل القرش الأزرق وكذلك جميع الأسماك الاستوائية الملونة.', N'يستثنى من ذلك منتجات مثل الحيتان والدلافين والقشريات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005468, N'Lagomorphs (أرانب / أرانب)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أي من الثدييات العاشبة المختلفة التي لها أقدام مكسوّة بالكامل وزوجان من القواطع العلوية وتنتمي إلى رتبة Lagomorpha. يشمل الأرانب والأرانب البرية والبيكا.', N'باستثناء الجرابيات والزواحف.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005467, N'الجرابيات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أي من الثدييات غير المشيمية المختلفة من رتبة Marsupialia. يشمل الكنغر ، الأبوسوم ، العصابات ، والومبات.', N'يستثنى من ذلك الزواحف و lagomorphs.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005465, N'غنم (غنم)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أي من الحيوانات المجترة ذات القرون عادة من جنس Ovis في عائلة Bovidae. يشمل كلا من الأنواع البرية والمستأنسة.', N'يستثنى من ذلك الماعز والخنازير وأي من منتجات الغنم مثل اللحوم أو الصوف.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005459, N'الرئيسيات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها كبيرة الأدمغة ، ومعظمها من الثدييات التي تعيش في الأشجار مع رؤية ألوان ثلاثية الأبعاد وإمساك اليدين. تشمل منتجات مثل القرود والليمور وكذلك البشر.', N'يستثنى من ذلك منتجات مثل الزواحف والبرمائيات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005466, N'الزواحف', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أي من الفقاريات ذوات الدم البارد ، والتي عادة ما تضع البيض من فئة الزواحف ، والتي لها غطاء خارجي من القشور أو لوحات قرنية وتتنفس عن طريق الرئتين. تشمل الأفاعي والسحالي والتماسيح والسلاحف.', N'يستثنى من ذلك الأسماك والبرمائيات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005455, N'القوارض', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حيوانات ذوات الدم الحار لها قاطعتان في الفك العلوي والسفلي تنموان باستمرار ويجب الحفاظ عليها بالتهالك عن طريق القضم. تشمل منتجات مثل الجرذان والفئران والسناجب والقنادس.', N'يستثنى من ذلك منتجات مثل الأرانب والقطط والكلاب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005464, N'الخنازير (الخنازير)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أي من الحيوانات النهمة ، متساوية الأصابع من عائلة Suidae ، والتي عادةً ما يكون لها جسم قوي البنية ، وجلد سميك ، وعنق قصير ، وخطم متحرك. تشمل منتجات مثل الخنازير والخنازير والخنازير.', N'يستثنى من ذلك منتجات مثل الماعز والأغنام وأي لحم خنزير.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005356, N'عبوات متنوعة لزيوت التشحيم', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمنتجات زيوت تشحيم مميزة أو أكثر يتم بيعها معًا ، والتي توجد ضمن المخطط الذي ينتمي إلى عائلات مختلفة ، أي منتجين أو أكثر متضمنين في نفس العبوة التي تتقاطع مع العائلات داخل قسم زيوت التشحيم. تشمل منتجات مثل زيت الفرامل ومضخة الزيت التي تُباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثنى من ذلك المنتجات مثل زيت المحرك مع مانع التجمد وحوض التصريف مع عبوات متنوعة لمضخة الزيت.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005283, N'عبوات متنوعة من مواد التشحيم / المركبات الواقية', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنهما اثنان أو أكثر من منتجات زيوت التشحيم / مركبات الحماية المميزة التي يتم بيعها معًا والتي توجد في المخطط ولكنها تنتمي إلى فئات مختلفة ، أي منتجين أو أكثر متضمنين في نفس العبوة والتي تتقاطع مع الفئات داخل مواد التشحيم / عائلة مركبات الحماية. يشمل منتجات مثل زيت المحرك ومضاد التجمد التي تباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثنى من ذلك منتجات مثل شحم التزليق مع مشتت المياه ومضاد التجمد مع عبوات متنوعة مضادة للتناثر.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005268, N'شحوم التزليق', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مادة زيتية ، لزجة ، وشبه صلبة ، يتم تداخلها بين سطحين أو أكثر للتحكم / تقليل الاحتكاك والتآكل. من خلال إنشاء فيلم بين أجزاء الآلة ، فإن هذه المنتجات تمكن الأسطح المتحركة من التحرك بسهولة أكبر ضد بعضها البعض. عادةً ما يتم استخدام هذه المنتجات عندما يتم تطبيق ضغوط شديدة على الأسطح وتكون الحركات متقطعة ، نظرًا لخصائصها اللاصقة والالتصاق. يشمل منتجات مثل شحوم الصوف وشحوم السيليكون.', N'يستثنى من ذلك منتجات مثل زيوت التشحيم السائلة / الزيتية والشمع ومضادات التآكل.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005267, N'زيوت / سوائل التشحيم', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها سائل أو زيت يتم تداخله بين سطحين أو أكثر للتحكم / تقليل الاحتكاك والتآكل. من خلال إنشاء فيلم بين أجزاء الآلة ، فإن هذه المنتجات تمكن الأسطح المتحركة من التحرك بسهولة أكبر ضد بعضها البعض. يشمل منتجات مثل زيت الفرامل وزيت المغزل.', N'يستثنى على وجه التحديد زيوت الطعام النباتية والحيوانية ، سائل ناقل الحركة وزيت المنظفات. يستثنى من ذلك منتجات مثل المزلقات التي تعتبر على وجه التحديد من المواد المضافة للوقود والشحوم والمواد المضادة للتآكل.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005270, N'عبوات متنوعة منتجات التشحيم', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنهما منتجان أو أكثر من منتجات التشحيم المميزة التي يتم بيعها معًا ، والتي توجد ضمن المخطط الذي ينتمي إلى طوب مختلف ولكن لنفس الفئة ، أي منتجين أو أكثر متضمنين في نفس العبوة التي تتقاطع مع الطوب داخل فئة منتجات التشحيم. يشمل منتجات مثل مشتت الماء وشحوم الصوف التي تباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثنى من ذلك المنتجات مثل زيوت التشحيم مع إضافات مضادة للتآكل وشمع التشحيم مع عبوات متنوعة مضادة للتناثر وزيت المحرك وحزم متنوعة من مواد التجمد.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005269, N'شموع التزليق', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مادة شمعية ، يتم وضعها على سطح أداة أو آلة أو سطح عمل أو أداة تثبيت لتقليل الاحتكاك والتآكل وتوفير حاجز وقائي. عادةً ما يتم تطبيق هذه المنتجات على الأدوات المستخدمة في قطع الأخشاب وحفرها ونشرها ورملها. تشمل منتجات مثل الشمع المطبق على السحابات على سفينة بحرية.', N'يستثنى على وجه التحديد شمع النحل وملمع الشمع. يستثنى من ذلك منتجات مثل زيوت التشحيم والشحوم وشمع الشمع.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005273, N'المضادة للتآكل', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مادة كيميائية توقف أو تبطئ تآكل أو صدأ المعادن والسبائك. أنها توفر طبقة واقية أو حاجز ضد الآثار الضارة من الرطوبة والهواء والمنظفات وغيرها من الملوثات. يشمل منتجات مثل مزيلات الصدأ.', N'يستثنى على وجه التحديد الدهانات ومواد التشحيم المضادة للتآكل ذات الخصائص / الفوائد المضادة للتآكل. يستثنى من ذلك المنتجات مثل إضافات الوقود المانعة للتآكل ومشتقات المياه ومانعات التسرب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005272, N'مانع التجمد / المبردات', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مستحضر سائل يضاف إلى الماء أو سائل آخر في نظام التبريد ، لخفض نقطة التجمد و / أو زيادة درجة الحرارة التي يغلي عندها. يشمل منتجات مثل المبردات المستخدمة في معدات اللحام.', N'يستثنى على وجه التحديد مانع التجمد والمبرد الخاص بالسيارات. يستثنى من ذلك المنتجات مثل المنتجات المضادة للتناثر والتآكل وزيوت التشحيم والسوائل والشحوم.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005321, N'منتجات مضادة للتناثر', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مستحضرات كيميائية تُستخدم على المواد التي يتم لحامها لحمايتها من بقع اللحام الملتصقة على سطحها. تستخدم هذه المنتجات أيضًا للحفاظ على الفوهات والعناصر الأخرى من مشاعل اللحام نظيفة وإطالة عمرها. يشمل منتجات مثل المعاجين وبخاخات السوائل.', N'يستثنى من ذلك منتجات مثل المواد المضادة للتآكل والمبردات وزيوت التشحيم والسوائل والشموع والشحوم.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005275, N'حزم متنوعة المركبات الواقية', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنهما منتجان أو أكثر من منتجات مجمع الحماية المميزة التي يتم بيعها معًا ، والتي توجد داخل المخطط الذي ينتمي إلى طوب مختلف ولكن لنفس الفئة ، أي منتجين أو أكثر متضمنين في نفس العبوة التي تتقاطع مع الطوب داخل فئة مركبات الحماية. تشمل منتجات مثل المنتجات المضادة للتآكل والمضادة للتناثر التي تُباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثنى من ذلك منتجات مثل مضاد التجمد مع الإضافات المضادة للتآكل وزيت المحرك وحزم متنوعة من مواد مضادة للتجمد.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005277, N'أحواض الصرف', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها صينية أو وعاء تم تصميمه خصيصًا للتجميع الآمن واستعادة الزيت المستخدم وسوائل التشحيم الأخرى. تشمل منتجات مثل أحواض الصرف البلاستيكية ذات فوهة السكب.', N'يستثنى على وجه التحديد صواني التنقيط المستخدمة لجمع القطرات والانسكابات من المشروبات. يستثنى من ذلك منتجات مثل حاويات تخزين الوقود والزيوت والممرات والقمع.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005276, N'حاويات / موزعات زيوت التشحيم (فارغة)', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حاوية أو جهاز تخزين فارغ ، والتي تم تصميمها خصيصًا لتكون قابلة لإعادة الملء وتحتفظ بها و / أو توزع مواد التشحيم. تشمل منتجات مثل براميل الزيت وعلب الزيت.', N'يستثنى على وجه التحديد الحاويات المصممة لتخزين الوقود. باستثناء منتجات مثل أحواض الصرف وحاويات الوقود والزجاجات وزجاجات تخزين زيت الطعام.')
