INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000663, N'عبوات متنوعة من أغذية / مشروبات الحيوانات الأليفة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجان أو أكثر من منتجات طعام وشراب الحيوانات الأليفة المميزة التي يتم بيعها معًا والموجودة في المخطط ولكنها تنتمي إلى فئات مختلفة ، أي منتجين أو أكثر متضمنين في نفس العبوة التي تتقاطع مع أغذية الحيوانات الأليفة وعائلة المشروبات. يشمل منتجات مثل طعام القطط مع عبوات متنوعة من مشروبات القطط. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'باستثناء منتجات مثل أغذية الطيور وحزم مغذيات الطيور المتنوعة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003982, N'مكيفات / مبردات - ثابتة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز ثابت أو مثبت للتحكم ، وخاصة لخفض درجة الحرارة والرطوبة في مكان مغلق.', N'باستثناء مكيفات الهواء المحمولة ومزيلات الرطوبة وأجهزة تنقية الهواء.')
