INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003984, N'قطع غيار / ملحقات أجهزة تكييف الهواء / التبريد / التهوية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جزء بديل أو ملحق لنظام تكييف الهواء المركب. يشمل منتجات مثل فلاتر الهواء لمكيف الهواء أو كتيفة الحائط لحمل جهاز التنفس الصناعي.', N'باستثناء منتجات مثل نظام تكييف الهواء الكامل.')
