INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003985, N'حزم متنوعة معدات تكييف الهواء / التبريد / التهوية', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنهما منتجان أو أكثر من منتجات تكييف الهواء / التبريد / التهوية المميزة التي تباع معًا ، والتي توجد داخل المخطط والتي تنتمي إلى طوب مختلف ولكن لنفس الفئة ، أي منتجين أو أكثر متضمنين في نفس العبوة التي تعبر الطوب ضمن فئة معدات التكييف / التبريد / التهوية. يشمل منتجات مثل نظام تهوية الهواء الذي يتم بيعه باستخدام معززات مجاري الهواء والأنابيب. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثنى من ذلك منتجات مثل مروحة الشفاط وحزم متنوعة للموقد.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003988, N'أجهزة تنقية الهواء / المؤينات - ثابتة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز ثابت أو مثبت ، والذي يهدف إلى تحرير الهواء من الملوثات. تشمل التقنيات المستخدمة لتنقية الهواء المواد الماصة الميكانيكية مثل الفحم والشحنات الكهروستاتيكية أو المؤينات.', N'باستثناء مكيفات الهواء المحمولة.')
