INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003995, N'مراوح - سقف', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مروحة معلقة من سقف الغرفة. تخلق هذه الأجهزة نسيمًا له تأثير تبريد ولكنها تساعد أيضًا في إعادة توزيع الهواء الدافئ. يشمل المنتجات المزودة بإضاءة مدمجة.', N'باستثناء مراوح الشفاط ومراوح النوافذ والمراوح المحمولة.')
