INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003998, N'مراوح - شفاط', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز مدمج يحتوي على مروحة كهربائية تساعد على تنفيس البخار أو الدخان أو الغاز أو الروائح من غرفة أو مبنى. تُستخدم هذه المنتجات في الغرف الصغيرة حيث يكون تدفق الهواء محدودًا ، كما هو الحال في الحمام أو المطبخ. يشمل المنتجات التي تحتوي على ضوء مدمج.', N'باستثناء مراوح السقف ومراوح النوافذ والمراوح المحمولة ومعززات مجاري الهواء.')
