INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005865, N'قطع غيار / ملحقات مانع التدفق العكسي', N'يشمل المنتجات التي يمكن وصفها / ملاحظتها كجزء أو ملحق مثل غطاء أو شفة أو صمام للاستخدام على وجه التحديد مع جهاز منع التدفق العكسي.', N'يستبعد جميع أجهزة منع التدفق العكسي الأخرى المصنفة.')
