INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002585, N'بيديه', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حوض منخفض مزود بمياه جارية باردة وساخنة تستخدم للنظافة التناسلية والعجان.', N'يستثنى من ذلك المنتجات مثل المراحيض والمباول.')
