INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007724, N'أذرع الاستحمام', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها تركيبات أنابيب مصنوعة لتوصيل مصدر المياه في جدار الدش برأس الدش.', N'باستثناء رؤوس الدش وقضبان الدش ومجموعات الدش.')
