INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002624, N'مصاعد الحمام', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز مخصص لمساعدة كبار السن أو المعاقين على الدخول إلى حوض الاستحمام والخروج منه. تعمل هذه المنتجات على إنزال الشخص إلى الحمام ، كما توفر الدعم أثناء الاستحمام. تشمل منتجات مثل المصاعد ذات الأحزمة والمصاعد القابلة للنفخ.', N'باستثناء منتجات مثل مقاعد البانيو / الدش وقضبان الدعم.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10004027, N'مقاعد الحمام', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مقعد أو كرسي ، مصنوع عادة من البلاستيك ، ويمكن وضعه في الحمام لتمكين كبار السن والمعاقين من الجلوس بشكل مريح في حوض الاستحمام. قد يكون المقعد قائمًا بذاته أو معلقًا من جوانب الحوض وقد يكون به وسيلة للدوران ، مما يتيح سهولة الدخول والخروج من الحمام.', N'باستثناء منتجات مثل رافعات الاستحمام وكراسي الحمام والمقاعد غير المصممة خصيصًا لحوض الاستحمام.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10004032, N'عبوات الحمام / الدش - ثابتة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حاوية مثبتة في الحمام أو جدار الدش تساعد في تخزين وتنظيم أدوات النظافة مثل الصابون والإسفنج ومعاجين الأسنان وفرشاة الأسنان. يشمل المنتجات التي يتم تركيبها بمساعدة ضمادات الشفط أو الأشرطة ذاتية اللصق أو التركيبات اللولبية.', N'يُستثنى من ذلك المنتجات مثل صحن الصابون القائم بذاته أو عبوة مستحضرات التجميل.')
