INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002623, N'أجهزة الإنذار الحرارية دش', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز يعرض درجة حرارة ماء الدش ويصدر إنذارًا مسموعًا عندما يكون الماء إما ساخنًا جدًا أو باردًا جدًا. تم تثبيت الإنذار بين وحدة الدش وخرطوم الدش. درجات الحرارة العلوية والسفلية قابلة للبرمجة ويمكن أيضًا عرض ساعة.', N'يستثنى من ذلك منتجات مثل ضوابط نظام التدفئة.')
