INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10004033, N'قضبان المناشف / الخطافات / الحلقات - ثابتة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها سكة تعليق أو خطاف أو حلقة مثبتة على جدار الحمام حيث يمكن تعليق منشفة الحمام / اليد ، بما في ذلك المنتجات مثل سكك المناشف المفردة والمزدوجة بالإضافة إلى قضبان المناشف التي تعمل بالغاز أو المسخن كهربائيًا . على وجه التحديد', N'يستثني: قضبان المناشف / الخطافات / الحلقات - غير مثبتة')
