INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007003, N'كابل تسخين / شريط حراري / سلك تسخين', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها كابل تسخين أو شريط تسخين أو سلك تسخين. يمكن استخدام هذه المنتجات في تتبع الأنابيب ، وحماية الأنابيب من التجمد ، والحفاظ على عملية درجة الحرارة ، والتحكم في لزوجة المنتجات الخام والبترولية.', N'يستثنى من ذلك المنتجات مثل الكابلات أو الأشرطة أو الأسلاك التي لا تُستخدم بأي شكل من الأشكال لتسخين خطوط الأنابيب. يستثنى على وجه التحديد السخانات الغاطسة المستخدمة في الغالب في الغلايات المنزلية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002662, N'حزم متنوعة معدات التدفئة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجين أو أكثر من منتجات معدات التسخين المميزة التي تباع معًا ، والتي توجد داخل المخطط الذي ينتمي إلى طوب مختلف ولكن لنفس الفئة ، أي منتجين أو أكثر موجودان في نفس العبوة ، والتي تتقاطع مع الطوب ضمن فئة معدات التدفئة. تشمل منتجات مثل المشعات التي تباع بصمامات الرادياتير. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثنى من ذلك منتجات مثل المبرد وحزم تعليق المناشف المتنوعة.')
